  static async create(product) {
    const { name, price, description } = product;
    const query = 'INSERT INTO products (name, price, description) VALUES (?, ?, ?)';
    const [result] = await connection.promise().query(query, [name, price, description]);
    return result.insertId;
  }

  // Get all products
  static async getAll() {
    const query = 'SELECT * FROM products';
    const [rows] = await connection.promise().query(query);
    return rows;
  }

  // Get a product by ID
  static async getById(id) {
    const query = 'SELECT * FROM products WHERE id = ?';
    const [rows] = await connection.promise().query(query, [id]);
    return rows[0];
  }

  // Update a product
  static async update(id, product) {
    const { name, price, description } = product;
    const query = 'UPDATE products SET name = ?, price = ?, description = ? WHERE id = ?';
    await connection.promise().query(query, [name, price, description, id]);
  }

  // Delete a product
  static async delete(id) {
